mapper.insert:
INSERT INTO paper (
	student_no
	, question_no
	, answer
	, answer_check
) VALUES (
	#{studentNo}
	,#{questionNo}
	,#{answer}
	,#{answerCheck}	
)

INSERT INTO student (
	student_id
	, student_pw
	, student_name
) VALUES (
	#{studentId}
	,PASSWORD(#{studentPw})
	,#{studentName}	
)

mapper.select:
SELECT student_no studentNo , student_id studentId , student_name studentName FROM student
student_name LIKE CONCAT('%', #{word}, '%')
ORDER BY student_no DESC LIMIT #{beginRow}, #{rowPerPage}
